import React, {Component} from 'react'
import LoadingBar from 'react-top-loading-bar'

export default class TopLoadingBar extends Component{
    constructor(){
        super();
        this.state = {
            progress: 50
        }
    }

    setProgress(setprogressval){
        this.setState({
            progress: setprogressval
        })
    }

    render(){
        return (
            <div>
                <LoadingBar
                    color='#FF0000'
                    progress={this.state.progress}
                    // onLoaderFinished={() => {this.setState({progress: 0})}}
                />
                <button onClick={() => {this.setProgress(this.state.progress+10)}}>Add 10%</button>
                <button onClick={() => {this.setProgress(this.state.progress+20)}}>Add 20%</button>
                <button onClick={() => {this.setProgress(100)}}>Complete</button>
                <br />
            </div>
        )
    }
}
